// Define defaults for missing values
const defaults = {
  primaryColor: '#4CAF50',   // Default color (green)
  accentColor: '#388E3C',
  iconType: 'success',        // Default icon (success)
  title: 'Notification',      // Default title
  bodyContent: '<p>Thank you for using our service.</p>',
  primaryButtonText: 'VIEW DETAILS',
  primaryButtonUrl: $('Global Constants').item.json.constants.WEBAPP_URL || '#',
  imageUrl: $('Global Constants').item.json.constants.IMAGE_URL || '',
  infoItems: []
};

// Set icon SVG based on type
let iconSvg = '';
const iconType = $input.item.json.iconType || defaults.iconType;

if (iconType === 'success') {
  // Success checkmark icon
  iconSvg = '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">\n' +
            '  <path d="M256 48C141.31 48 48 141.31 48 256s93.31 208 208 208 208-93.31 208-208S370.69 48 256 48zm108.25 138.29l-134.4 160a16 16 0 01-12 5.71h-.27a16 16 0 01-11.89-5.3l-57.6-64a16 16 0 1123.78-21.4l45.29 50.32 122.59-145.91a16 16 0 0124.5 20.58z"/>\n' +
            '</svg>';
} else if (iconType === 'error') {
  // Error/warning icon
  iconSvg = '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">\n' +
            '  <path d="M256 48C141.31 48 48 141.31 48 256s93.31 208 208 208 208-93.31 208-208S370.69 48 256 48zm0 319.91a20 20 0 1 1 20-20 20 20 0 0 1-20 20zm21.72-201.15l-5.74 122a16 16 0 0 1-32 0l-5.74-121.94v-.05a21.74 21.74 0 1 1 43.44 0z"/>\n' +
            '</svg>';
} else if (iconType === 'info') {
  // Info icon
  iconSvg = '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">\n' +
            '  <path d="M256 48C141.31 48 48 141.31 48 256s93.31 208 208 208 208-93.31 208-208S370.69 48 256 48zm0 82a26 26 0 1 1-26 26 26 26 0 0 1 26-26zm48 226h-88a16 16 0 0 1 0-32h28v-88h-16a16 16 0 0 1 0-32h32a16 16 0 0 1 16 16v104h28a16 16 0 0 1 0 32z"/>\n' +
            '</svg>';
}

// Get the color theme from input or use default
const primaryColor = $input.item.json.primaryColor || defaults.primaryColor;
const accentColor = $input.item.json.accentColor || defaults.accentColor;

// Generate info items HTML
let infoItemsHtml = '';
const infoItems = $input.item.json.infoItems || defaults.infoItems;

if (infoItems && infoItems.length > 0) {
  infoItems.forEach(item => {
    infoItemsHtml += `
      <div class="info-item">
        <span class="info-label">${item.label}</span>
        <span class="info-value">${item.value}</span>
      </div>
    `;
  });
}

// Apply all values (from input or defaults)
$input.item.json.iconSvg = iconSvg;
$input.item.json.primaryColor = primaryColor;
$input.item.json.accentColor = accentColor;
$input.item.json.title = $input.item.json.title || defaults.title;
$input.item.json.bodyContent = $input.item.json.bodyContent || defaults.bodyContent;
$input.item.json.primaryButtonText = $input.item.json.primaryButtonText || defaults.primaryButtonText;
$input.item.json.primaryButtonUrl = $input.item.json.primaryButtonUrl || defaults.primaryButtonUrl;
$input.item.json.imageUrl = $input.item.json.imageUrl || $('Global Constants').item.json.constants.IMAGE_URL || '';
$input.item.json.infoItemsHtml = infoItemsHtml;

return $input.item;

parameters.html:
=<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>{{ $json.title }} - ZevenGlobal</title>
    <style>
        @import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@400;500;600;700&display=swap');

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Montserrat', sans-serif;
        }

        body {
            background-color: #f0f5ff;
            padding: 40px 20px;
        }

        .email-container {
            max-width: 650px;
            margin: 0 auto;
            background: #0a1a35;
            border-radius: 16px;
            overflow: hidden;
            box-shadow: 0 12px 30px rgba(0, 65, 158, 0.2);
        }

        .top-header {
            background-color: #0a1730;
            padding: 15px 0;
            text-align: center;
            border-bottom: 1px solid rgba(100, 150, 255, 0.2);
        }

        .top-header img {
            max-width: 120px;
            height: auto;
        }

        .hero-section {
            background-color: #101f3d;
            padding: 30px 20px;
            text-align: center;
            color: white;
        }

        .hero-section h1 {
            font-size: 24px;
            margin-bottom: 10px;
            color: {{ $json.primaryColor }};
            font-weight: 700;
        }

        .hero-section p {
            font-size: 15px;
            line-height: 1.5;
            max-width: 450px;
            margin: 0 auto;
            color: #b4c9ff;
        }

        .content {
            padding: 30px 25px;
            position: relative;
        }

        .notification-icon {
            text-align: center;
            margin-bottom: 15px;
        }

        .notification-icon svg {
            width: 40px;
            height: 40px;
            fill: {{ $json.primaryColor }};
        }

        .notification-card {
            position: relative;
            z-index: 1;
            background: rgba(18, 37, 73, 0.9);
            border-radius: 10px;
            padding: 25px 20px;
            margin-bottom: 25px;
            border: 1px solid rgba({{ $json.primaryColor.replace('#', '').match(/.{1,2}/g).map(hex => parseInt(hex, 16)).join(', ') }}, 0.3);
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.15);
        }

        .notification-card h2 {
            color: white;
            font-size: 20px;
            margin-bottom: 15px;
            padding-bottom: 12px;
            border-bottom: 1px solid rgba({{ $json.primaryColor.replace('#', '').match(/.{1,2}/g).map(hex => parseInt(hex, 16)).join(', ') }}, 0.2);
            text-align: center;
        }

        .notification-message {
            color: white;
            text-align: center;
            margin-bottom: 20px;
            font-size: 15px;
            line-height: 1.5;
        }

        .info-item {
            display: flex;
            align-items: center;
            padding: 12px 0;
            border-bottom: 1px solid rgba(100, 150, 255, 0.1);
        }

        .info-item:last-child {
            border-bottom: none;
        }

        .info-label {
            color: #7e9bff;
            font-weight: 500;
            font-size: 14px;
            width: 40%;
            padding-right: 15px;
        }

        .info-value {
            color: white;
            font-weight: 600;
            font-size: 15px;
            background-color: rgba({{ $json.primaryColor.replace('#', '').match(/.{1,2}/g).map(hex => parseInt(hex, 16)).join(', ') }}, 0.1);
            padding: 8px 12px;
            border-radius: 4px;
            border: 1px solid rgba({{ $json.primaryColor.replace('#', '').match(/.{1,2}/g).map(hex => parseInt(hex, 16)).join(', ') }}, 0.2);
            word-break: break-word;
            width: 60%;
            flex-grow: 1;
        }

        .action-button {
            display: block;
            background-color: {{ $json.primaryColor }};
            color: white;
            text-decoration: none;
            padding: 14px 20px;
            border-radius: 6px;
            font-size: 16px;
            font-weight: 600;
            text-align: center;
            margin: 20px auto 10px;
            max-width: 250px;
            transition: all 0.3s ease;
            box-shadow: 0 4px 8px rgba({{ $json.primaryColor.replace('#', '').match(/.{1,2}/g).map(hex => parseInt(hex, 16)).join(', ') }}, 0.3);
        }

        .action-button:hover {
            background-color: {{ $json.accentColor }};
        }

        .footer {
            background-color: #0a1730;
            padding: 20px;
            text-align: center;
            color: #7e9bff;
            font-size: 13px;
            border-top: 1px solid rgba(100, 150, 255, 0.2);
        }

        .social-links {
            margin: 12px 0;
        }

        .social-links a {
            display: inline-block;
            width: 30px;
            height: 30px;
            background-color: rgba(100, 150, 255, 0.15);
            border-radius: 50%;
            margin: 0 5px;
            line-height: 30px;
            color: #7e9bff;
            text-align: center;
        }

        .social-icon {
            width: 16px;
            height: 16px;
            vertical-align: middle;
        }

        @media (max-width: 480px) {
            .email-container {
                border-radius: 10px;
            }

            .top-header img {
                max-width: 100px;
            }

            .hero-section h1 {
                font-size: 20px;
            }

            .hero-section p {
                font-size: 14px;
            }

            .notification-card {
                padding: 20px 15px;
            }

            .info-item {
                flex-direction: column;
                align-items: flex-start;
            }

            .info-label {
                width: 100%;
                margin-bottom: 6px;
            }

            .info-value {
                width: 100%;
            }

            .action-button {
                width: 100%;
                padding: 12px 15px;
                font-size: 15px;
            }
        }
    </style>
</head>
<body>
    <div class="email-container">
        <div class="top-header">
            <img src="{{ $json.imageUrl }}" alt="ZevenGlobal Logo">
        </div>

        <div class="hero-section">
            <h1>{{ $json.title }}</h1>
            <p>{{ $json.subtitle }}</p>
        </div>

        <div class="content">
            <div class="notification-icon">
                {{ $json.iconSvg }}
            </div>

            <div class="notification-card">
                <h2>{{ $json.cardTitle || 'Details' }}</h2>

                <div class="notification-message">
                    {{ $json.bodyContent }}
                </div>

                {{ $json.infoItemsHtml }}
            </div>

            <p style="color: white; text-align: center; margin-bottom: 15px; font-size: 14px;">{{ $json.ctaText }}</p>

            <a href="{{ $json.primaryButtonUrl }}" target="_blank" class="action-button">{{ $json.primaryButtonText }}</a>
        </div>

        <div class="footer">
            <p>© {{ new Date().getFullYear() }} ZevenGlobal - All rights reserved</p>
            <div class="social-links">
                <a href="#">
                    <img class="social-icon" src="https://cdn-icons-png.flaticon.com/512/124/124010.png" alt="Facebook">
                </a>
                <a href="#">
                    <img class="social-icon" src="https://cdn-icons-png.flaticon.com/512/124/124021.png" alt="Twitter">
                </a>
                <a href="#">
                    <img class="social-icon" src="https://cdn-icons-png.flaticon.com/512/174/174855.png" alt="Instagram">
                </a>
            </div>
            <p>If you have any questions, contact us at <a href="mailto:{{ $('Global Constants').item.json.constants.EMAIL }}" style="color: #3a9fff; text-decoration: none;">{{ $('Global Constants').item.json.constants.EMAIL }}</a></p>
        </div>
    </div>
</body>
</html>
