// Obtener la data original
const originalData = $('Escoger Metadata').item.json.data;

// Verificar el tipo de datos y procesarlos adecuadamente
let result;

if (Array.isArray(originalData)) {
  // Si es un array, usamos map
  result = originalData.map(item => {return {
    daysSinceTradingStarted: item.daysSinceTradingStarted
  };});
} else if (originalData && typeof originalData === 'object') {
  // Si es un objeto (no array), extraemos directamente la propiedad
  result = {"daysSinceTradingStarted":Math.floor(originalData.daysSinceTradingStarted||0),
           "maxDrawdown":originalData.maxDrawdown || 0,
           "profit":originalData.profit || 0,
            "today_profit":$json.data.periods.today?.profit || 0
}} else {
  // Si no es ni array ni objeto, devolvemos un mensaje de error
  result = { error: 'La estructura de datos no es la esperada' };
}

// Para debugging: ver qué tipo de dato estamos manejando
console.log('Tipo de originalData:', typeof originalData);
console.log('Es array?', Array.isArray(originalData));
console.log('Valor de originalData:', JSON.stringify(originalData).substring(0, 200) + '...');

return ({"data":result});

const allData = [];
let counter = 0;
do {
  try {
    // Obtener los items del nodo de n8n
    const items = $("Obtener Challenges activos").all(0, counter);

    // Si no hay items, salir del bucle
    if (!items || items.length === 0) {
      break;
    }

    // Extraer los datos del array 'data' dentro de json de cada item
    const extractedData = items.map(item => item.json.data).flat();

    // Agregar los datos extraídos al array final
    allData.push.apply(allData, extractedData);
  } catch (error) {
    // Devolver los datos recolectados hasta ahora
    return ({"data":allData});
  }
  counter++;
} while(true);

return ({"data":allData});

